//Fetching OpenWeather API (Weather Data of Some Places)

const weatherAPIKey = "391cbb4a58a8cc222403d449bb858880";
let city = prompt("Please Provide the City that You Would Like the Weather To?");
const queryURL = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${weatherAPIKey}`;
let savedJSON;
// let errorInName = false;
fetch (queryURL)
  .then(response => {
      savedJSON = response.json();
      return response.json();
    })
  .then(data => console.log(`Weather in ${city} is ${data.main.temp}`))
  .catch(error => {
    // errorInName = true;
    // return error;
    console.error(`Error: ${error}`);

  });


// if (errorInName) {
